/**
 * @fileoverview ExamFlowAnimation - Interactive demo of cohort examination flow.
 * 
 * This component demonstrates the real-time examination management system used in
 * the Namwon Cohort Study. It visualizes the flow between tablet input and status
 * board updates with TTS announcements.
 * 
 * @module features/portfolio/projects/namwon-cohort/ExamFlowAnimation
 * @see {@link StatusBoard} - Real-time examination status display
 * @see {@link TabletScreen} - Tablet interface for patient selection
 * @see {@link TTSAnnouncement} - Text-to-speech notification bubble
 */

"use client";

import { useState, useEffect, useMemo } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { StatusBoard } from "./subcomponents/StatusBoard";
import { TabletScreen } from "./subcomponents/TabletScreen";
import { TTSAnnouncement } from "./subcomponents/TTSAnnouncement";
import { useTranslations } from "next-intl";
import { BrowserFrame } from "@/features/portfolio/components/BrowserFrame";
import { useMobile } from "@/hooks/useMobile";
import { cn } from "@/lib/utils";

// --- Constants & Configuration ---

/**
 * Configuration constants for animation timings, scaling, and positioning.
 * All timing values are in milliseconds.
 */
const CONSTANTS = {
    /**
     * Duration of each animation phase in milliseconds.
     * Controls the pace of the demo cycle.
     */
    TIMINGS: {
        /** Focus on tablet (user looking at tablet) */
        "tablet-focus": 2500,
        /** Clicking animation on tablet */
        "tablet-clicking": 700,
        /** Transition focus to status board */
        "statusboard-focus": 1000,
        /** Blinking indicator on status board */
        "statusboard-blink": 1500,
        /** Status board data update animation */
        "statusboard-update": 1200,
        /** TTS announcement display duration */
        "tts-announce": 2500,
    },
    /**
     * Scale factors for responsive display.
     * Mobile uses 0.7x container scale with inverse percentage sizing.
     */
    SCALE: {
        MOBILE_CONTAINER: 0.7,
        MOBILE_WIDTH: '142.86%',
        MOBILE_HEIGHT: '142.86%',
        DEFAULT_WIDTH: '100%',
        DEFAULT_HEIGHT: '100%',
        /** Default scale when embedded in other components */
        EMBEDDED_DEFAULT: 0.55,
    },
    /**
     * Position and style configurations for tablet/board front/back states.
     * Uses spring animation for smooth card-flip-like transitions.
     */
    STYLES: {
        TABLET_FRONT: {
            TOP: '3%', LEFT: '3%', WIDTH: '94%', SCALE: 1, FILTER: 'brightness(1) blur(0px)', Z: 30
        },
        TABLET_BACK: {
            TOP: '12%', LEFT: '12%', WIDTH: '76%', SCALE: 0.92, FILTER: 'brightness(0.95) blur(2px)', Z: 10
        },
        BOARD_FRONT: {
            TOP: '3%', LEFT: '3%', WIDTH: '94%', SCALE: 1, FILTER: 'brightness(1) blur(0px)', Z: 30
        },
        BOARD_BACK: {
            TOP: '12%', LEFT: '12%', WIDTH: '76%', SCALE: 0.92, FILTER: 'brightness(0.95) blur(2px)', Z: 10
        }
    }
} as const;

/**
 * Mock patient data for demonstration.
 * Represents realistic cohort study participants with Korean names.
 */
const PATIENT_LIST = [
    { id: 'NW240101', name: '源?쒖옄', birth: '1945-03-12' },
    { id: 'NW240102', name: '?댁쁺??, birth: '1948-07-25' },
    { id: 'NW240103', name: '諛뺤젙??, birth: '1952-11-08' },
    { id: 'NW240104', name: '理쒕???, birth: '1950-02-19' },
    { id: 'NW240105', name: '?뺤쁺??, birth: '1947-09-03' },
    { id: 'NW240106', name: '?쒖긽泥?, birth: '1953-06-14' },
];

/**
 * Initial status board state showing SNSB-C rooms and examination stations.
 * Displays current patient, elapsed time, and next patient for each station.
 */
const BOARD_STATE = {
    snsbcRooms: [
        { roomNum: 1, current: '源?쒖옄', time: '18:45', next: '?뺤쁺?? },
        { roomNum: 2, current: '?댁쁺??, time: '12:30', next: '?쒖긽泥? },
        { roomNum: 3, current: '諛뺤젙??, time: '08:15', next: null },
    ],
    stations: {
        ecg: { current: null, time: '-', next: '理쒕??? },
        blood: { current: null, time: '-', next: '源?쒖옄' },
        physical: { current: '理쒕???, time: '04:22', next: '?쒖긽泥? },
    }
};

/**
 * Animation phase states representing the demo cycle.
 * Progresses: tablet-focus ??tablet-clicking ??statusboard-focus ??
 *             statusboard-blink ??statusboard-update ??tts-announce ??(loop)
 */
type AnimationPhase = 
    | "tablet-focus" 
    | "tablet-clicking" 
    | "statusboard-focus" 
    | "statusboard-blink" 
    | "statusboard-update"
    | "tts-announce";

/**
 * Props for ExamFlowAnimation component.
 */
interface ExamFlowAnimationProps {
    /** Whether component is embedded within another container (affects scaling) */
    isEmbedded?: boolean;
    /** Override scale factor. If not provided, uses EMBEDDED_DEFAULT or 1. */
    scale?: number;
    /** Additional CSS classes for the container */
    className?: string;
    /** Whether animation is active. Set false to pause. */
    isActive?: boolean;
    /** @deprecated Use internal useMobile hook instead */
    isMobile?: boolean; 
}

/**
 * Interactive demo component showcasing the real-time cohort examination flow.
 * 
 * Demonstrates the complete workflow:
 * 1. Staff selects patient on tablet
 * 2. Status board updates in real-time
 * 3. TTS announces next patient
 * 
 * @component
 * @example
 * // Embedded in portfolio page
 * <ExamFlowAnimation isEmbedded scale={0.8} />
 * 
 * @example
 * // Standalone with pause control
 * <ExamFlowAnimation isActive={isPlaying} />
 */
export function ExamFlowAnimation({
    isEmbedded = false,
    scale,
    className,
    isActive = true,
    // isMobile = false // internal hook used instead
}: ExamFlowAnimationProps) {
    const isMobile = useMobile();
    const t = useTranslations("CohortDashboard");
    const tExam = useTranslations("CohortDashboard.ExamFlow");

    const [currentStep, setCurrentStep] = useState(0);
    const [phase, setPhase] = useState<AnimationPhase>("tablet-focus");
    const [blinkingStation, setBlinkingStation] = useState<string | null>(null);
    const [showNextUpdate, setShowNextUpdate] = useState(false);
    const [blinkToggle, setBlinkToggle] = useState(false);

    const EXAM_SCENARIOS = useMemo(() => [
        { 
            type: "complete" as const,
            examRoom: t('Bottleneck.exams.body'),
            stationId: "physical",
            currentPerson: "理쒕???

start" as const,
            examRoom: t('Bottleneck.exams.blood'),
            stationId: "blood",
            currentPerson: "源?쒖옄",
            nextPerson: "源?쒖옄",
            ttsText: tExam('TTS.message', {name: "源?쒖옄", room: t('Bottleneck.exams.blood')}),
        },
    ], [t, tExam]);

    const currentExam = EXAM_SCENARIOS[currentStep];
    const currentScale = scale ?? (isEmbedded ? CONSTANTS.SCALE.EMBEDDED_DEFAULT : 1);

    useEffect(() => {
        if (!blinkingStation || !isActive) {
            setBlinkToggle(false);
            return;
        }
        const interval = setInterval(() => {
            setBlinkToggle(prev => !prev);
        }, 600);
        return () => clearInterval(interval);
    }, [blinkingStation, isActive]);

    useEffect(() => {
        if (!isActive) return;

        const timer = setTimeout(() => {
            switch (phase) {
                case "tablet-focus":
                    setPhase("tablet-clicking");
                    break;
                case "tablet-clicking":
                    setPhase("statusboard-focus");
                    break;
                case "statusboard-focus":
                    setBlinkingStation(currentExam.stationId);
                    setPhase("statusboard-blink");
                    break;
                case "statusboard-blink":
                    setShowNextUpdate(true);
                    setPhase("statusboard-update");
                    break;
                case "statusboard-update":
                    setPhase("tts-announce");
                    break;
                case "tts-announce":
                    setBlinkingStation(null);
                    setShowNextUpdate(false);
                    setCurrentStep((prev) => (prev + 1) % EXAM_SCENARIOS.length);
                    setPhase("tablet-focus");
                    break;
            }
        }, CONSTANTS.TIMINGS[phase]);

        return () => clearTimeout(timer);
    }, [phase, currentExam.stationId, isActive, EXAM_SCENARIOS.length]);

    const isTabletFront = phase === "tablet-focus" || phase === "tablet-clicking";

    const content = (
        <div className="w-full h-full relative overflow-hidden">
            <div className="w-full h-full" style={{
                width: isMobile ? CONSTANTS.SCALE.MOBILE_WIDTH : CONSTANTS.SCALE.DEFAULT_WIDTH,
                height: isMobile ? CONSTANTS.SCALE.MOBILE_HEIGHT : CONSTANTS.SCALE.DEFAULT_HEIGHT,
                transform: isMobile ? `scale(${CONSTANTS.SCALE.MOBILE_CONTAINER})` : 'none',
                transformOrigin: 'top left'
            }}>
                {/* Base Background Decor Removed */}
            
            {/* Status Board - positioned to bottom-right */}
            <motion.div
                className="absolute rounded-xl shadow-[0_30px_60px_-12px_rgba(0,0,0,0.15)] border border-white overflow-hidden ring-1 ring-slate-200"
                animate={{
                    top: isTabletFront ? CONSTANTS.STYLES.BOARD_BACK.TOP : CONSTANTS.STYLES.BOARD_FRONT.TOP,
                    left: isTabletFront ? CONSTANTS.STYLES.BOARD_BACK.LEFT : CONSTANTS.STYLES.BOARD_FRONT.LEFT,
                    width: isTabletFront ? CONSTANTS.STYLES.BOARD_BACK.WIDTH : CONSTANTS.STYLES.BOARD_FRONT.WIDTH,
                    height: '85%',
                    zIndex: isTabletFront ? CONSTANTS.STYLES.BOARD_BACK.Z : CONSTANTS.STYLES.BOARD_FRONT.Z,
                    scale: isTabletFront ? CONSTANTS.STYLES.BOARD_BACK.SCALE : CONSTANTS.STYLES.BOARD_FRONT.SCALE,
                    filter: isTabletFront ? CONSTANTS.STYLES.BOARD_BACK.FILTER : CONSTANTS.STYLES.BOARD_FRONT.FILTER,
                }}
                transition={{ type: "spring", stiffness: 350, damping: 35 }}
            >
                <StatusBoard 
                    boardState={BOARD_STATE}
                    blinkingStation={blinkingStation}
                    blinkToggle={blinkToggle}
                    showNextUpdate={showNextUpdate}
                    currentExam={currentExam}
                />
            </motion.div>

            {/* Tablet Screen - positioned to top-left */}
            <motion.div
                className="absolute rounded-2xl shadow-[0_30px_60px_-12px_rgba(0,0,0,0.15)] border border-white overflow-hidden ring-1 ring-slate-200"
                style={{ height: '85%' }}
                animate={{
                    top: isTabletFront ? CONSTANTS.STYLES.TABLET_FRONT.TOP : CONSTANTS.STYLES.TABLET_BACK.TOP,
                    left: isTabletFront ? CONSTANTS.STYLES.TABLET_FRONT.LEFT : CONSTANTS.STYLES.TABLET_BACK.LEFT,
                    width: isTabletFront ? CONSTANTS.STYLES.TABLET_FRONT.WIDTH : CONSTANTS.STYLES.TABLET_BACK.WIDTH,
                    zIndex: isTabletFront ? CONSTANTS.STYLES.TABLET_FRONT.Z : CONSTANTS.STYLES.TABLET_BACK.Z,
                    scale: isTabletFront ? CONSTANTS.STYLES.TABLET_FRONT.SCALE : CONSTANTS.STYLES.TABLET_BACK.SCALE,
                    filter: isTabletFront ? CONSTANTS.STYLES.TABLET_FRONT.FILTER : CONSTANTS.STYLES.TABLET_BACK.FILTER,
                }}
                transition={{ type: "spring", stiffness: 350, damping: 35 }}
            >
                <TabletScreen 
                    currentExam={currentExam}
                    patientList={PATIENT_LIST}
                    phase={phase}
                />
            </motion.div>

            {/* TTS Speech Bubble */}
            <AnimatePresence>
                {phase === "tts-announce" && (
                    <TTSAnnouncement ttsText={currentExam.ttsText} />
                )}
            </AnimatePresence>
            </div>
        </div>
    );

    if (isEmbedded) {
        return (
            <div 
                className={cn(`w-full h-full`, className)}
                style={{ transform: `scale(${currentScale})`, transformOrigin: 'center' }}
            >
                {content}
            </div>
        );
    }

    return (
        <section className="py-24 bg-white overflow-hidden">
            <div className="max-w-5xl mx-auto px-6">
                <div className="text-center mb-12">
                    <div className="inline-block px-3 py-1 bg-blue-50 text-blue-600 text-[10px] font-black uppercase tracking-widest rounded-full mb-4 ring-1 ring-blue-100">
                        {tExam('automationLabel')}
                    </div>
                    <h2 className="text-3xl font-black text-slate-900 mb-4 tracking-tight">
                        {tExam('title')}
                    </h2>
                    <p className="text-slate-500 max-w-2xl mx-auto text-base leading-relaxed" dangerouslySetInnerHTML={{ __html: tExam.raw('description') }} />
                </div>

                <div className="flex justify-center">
                    <div className="w-full max-w-[900px] aspect-[16/10] relative group">
                        <BrowserFrame url="cohort.dashboard.local" className="w-full h-full" uiScale={1}>
                            {content}
                        </BrowserFrame>
                    </div>
                </div>
            </div>
        </section>
    );
}

