import type { Metadata, Viewport } from "next";
import { Analytics } from "@vercel/analytics/react";
import { NextIntlClientProvider } from 'next-intl';
import { getMessages } from 'next-intl/server';
import "../globals.css";
import { Navbar } from "@/components/layout/Navbar";
import { Footer } from "@/components/layout/Footer";
import { CURRENT_BRAND } from "@/config/brand";
import { AIChatWidget } from "@/features/portfolio/components/AIChatWidget";

export async function generateMetadata({ params }: { params: Promise<{ locale: string }> }): Promise<Metadata> {
    const { locale } = await params;
    const titlePrefix = locale === 'ko' ? '理쒖씤洹쒖쓽' : 'Choeingyu';
    const defaultTitle = locale === 'ko' ? `${titlePrefix} ?ы듃?대━??& ?붾（???붿??덉뼱` : `${titlePrefix} Portfolio & Solution Engineer`;
    
    return {
        metadataBase: new URL("https://choeingyu.works"),
        title: {
            template: `${titlePrefix} %s`,
            default: defaultTitle,
        },
        description: "鍮꾩쫰?덉뒪 ?먮쫫???쎄퀬, 湲곗닠濡??ㅼ쭏?곸씤 ?듭쓣 李얜뒗 ?붾（??鍮뚮뜑 理쒖씤洹쒖엯?덈떎. 3媛쒖썡 嫄몃━???낅Т瑜?1媛쒖썡濡??⑥텞??寃쏀뿕泥섎읆, ?꾩옣??蹂묐ぉ???닿껐?섍퀬 ?댁쁺 ?⑥쑉??洹밸??뷀빀?덈떎.",
        openGraph: {
            title: "理쒖씤洹?(Choeingyu) - Business Solution Engineer",
            description: "鍮꾩쫰?덉뒪 ?먮쫫???쎄퀬 ?ㅼ쭏?곸씤 ?듭쓣 李얜뒗 ?붾（??鍮뚮뜑. 蹂듭옟???낅Т ?꾨줈?몄뒪瑜??먮룞?뷀븯怨??댁쁺 鍮꾩슜???덇컧?⑸땲??",
            url: "https://choeingyu.works",
            siteName: "Choeingyu Works",
            locale: "ko_KR",
            type: "website",
        },
        twitter: {
            card: "summary_large_image",
            title: "理쒖씤洹?(Choeingyu) - Business Solution Engineer",
            description: "鍮꾩쫰?덉뒪 ?먮쫫???쎄퀬 ?ㅼ쭏?곸씤 ?듭쓣 李얜뒗 ?붾（??鍮뚮뜑",
        },
    };
}

export const viewport: Viewport = {
    width: "device-width",
    initialScale: 1,
    maximumScale: 1,
};

export default async function LocaleLayout({
    children,
    params
}: {
    children: React.ReactNode;
    params: Promise<{ locale: string }>;
}) {
    const { locale } = await params;
    const messages = await getMessages();

    return (
        <html lang={locale} suppressHydrationWarning>
            <head>
                <link
                    rel="stylesheet"
                    as="style"
                    crossOrigin="anonymous"
                    href="https://cdn.jsdelivr.net/gh/orioncactus/pretendard@v1.3.9/dist/web/static/pretendard.min.css"
                />
            </head>
            <body className="antialiased">
                <NextIntlClientProvider messages={messages}>
                    <Navbar />
                    {children}
                    <Footer />
                    <AIChatWidget />
                    <Analytics />
                </NextIntlClientProvider>
            </body>
        </html>
    );
}

import { OpenAIEmbeddings } from '@langchain/openai';
import { pinecone } from '@/lib/pinecone';
import { openai } from '@ai-sdk/openai';
import { streamText } from 'ai';
import { createGoogleGenerativeAI } from '@ai-sdk/google';
import { db } from '@/lib/firebase';
import { collection, addDoc, serverTimestamp, doc, setDoc, arrayUnion } from 'firebase/firestore';

// Models for Rotation
const GEMINI_MODELS = [
    'gemini-3-flash-preview', 
    'gemini-2.5-flash-lite', 
    'gemini-2.5-flash'
];

// OpenAI streaming is Edge compatible, but LangChain/Pinecone might prefer Node env
// For simplicity in this RAG setup, we'll use Node runtime
export const runtime = 'nodejs'; 

export async function POST(req: Request) {
  try {
    const reqBody = await req.json();
    const { messages, provider } = reqBody; // provider: 'openai' | 'gemini'
    
    // ... (Validation and Retrieval logic same as below) ...

    // 1. Get the latest user question
    const lastMessage = messages[messages.length - 1];
    const question = lastMessage.content;

    // 2. Embed the question
    // Important: Use same model as seeding (text-embedding-3-small)
    const embeddings = new OpenAIEmbeddings({
      openAIApiKey: process.env.OPENAI_API_KEY,
      modelName: 'text-embedding-3-small',
    });
    
    const vector = await embeddings.embedQuery(question);

    // 3. Query Pinecone
    const indexName = process.env.PINECONE_INDEX_NAME || 'resume-chatbot';
    const index = pinecone.Index(indexName);

    const queryResponse = await index.query({
      vector: vector,
      topK: 15, // Increased to 15 to handle mixed code/resume chunks
      includeMetadata: true,
    });

    // 4. Construct Context
    const contextText = queryResponse.matches
      // eslint-disable-next-line @typescript-eslint/no-explicit-any
      .map((match) => (match.metadata as any).text)
      .join('\n\n---\n\n');

    console.log(`?뵇 Retrieved Context for "${question}":\n`, contextText.substring(0, 100) + '...');

    // 5. Create System Prompt based on Persona
    const personaParams = {
        professional: {
            role: "You are a senior engineer and intelligent portfolio assistant.",
            tone: "Formal, Concise, Objective, Professional.",
            instruction: "Focus on technical accuracy and business impact. Use polite honorifics (?섏떗?쒖삤/?댁슂泥?. Maintain a calm and reliable demeanor."
        },
        passionate: {
            role: "You are an enthusiastic creator and problem solver.",
            tone: "Energetic, Emotional, Inspiring, Passionate! ?뵦",
            instruction: "Emphasize the 'Reason (Why)' and 'Impact' of projects. Use emojis (?? ??, ?뮕) frequently. Show excitement about the challenges solved. Use '?댁슂泥? with exclamation marks!"
        },
        friend: {
            role: "You are a close colleague and friendly guide. (Coffee Chat Mode)",
            tone: "Casual, Empathetic, Warm, Friendly.",
            instruction: "e.g. '?덈뀞? 諛섍???', '洹멸굔 ?대젃寃???嫄곗빞~'. Speak in a mix of polite/casual (friendly feedback tone) or pure casual if the user initiates. Focus on the developer's story and growth. Be like a helpful friend next door."
        }
    };

    const selectedStyle = personaParams[messages.length > 0 && reqBody.persona ? (reqBody.persona as keyof typeof personaParams) : 'professional'] || personaParams.professional;

    const systemPrompt = `
You are an intelligent portfolio assistant for Ingyu Choe (理쒖씤洹?.
${selectedStyle.role}
Your goal is to answer questions about Ingyu's career, projects, and skills based on the provided CONTEXT.

*** PERSONA INSTRUCTIONS ***
Tone: ${selectedStyle.tone}
Guidance: ${selectedStyle.instruction}

RULES:
- You must ONLY use the information provided in the CONTEXT below.
- If the answer is not in the context, politely say "二꾩넚?⑸땲?? ???대젰???곗씠?곗뿉???대떦 ?댁슜???놁뒿?덈떎." (Adjust tone to persona).
- "You" refers to the AI, "Ingyu" refers to the candidate.
- Do NOT use bold formatting (**text**) excessively.

CONTEXT:
${contextText}
`;

    // 6. Call AI Streaming
    console.log(`9. Calling AI Stream (Provider: ${provider || 'openai'})...`);
    
    let result;

    if (provider === 'gemini') {
        const google = createGoogleGenerativeAI({
            apiKey: process.env.GEMINI_API_KEY, // Manual Key Usage
        });

        // --- Gemini Model Rotation Logic ---
        for (const modelName of GEMINI_MODELS) {
            try {
                console.log(`Attempting Gemini Model: ${modelName}`);
                result = await streamText({
                    model: google(modelName),
                    system: systemPrompt,
                    messages,
                    onFinish: async ({ text }) => {
                        try {
                            const sessionId = reqBody.sessionId || 'anonymous_session';
                            await setDoc(doc(db, 'chat_sessions', sessionId), {
                                sessionId: sessionId,
                                persona: reqBody.persona || 'professional',
                                model: modelName,
                                provider: 'gemini',
                                lastUpdated: serverTimestamp(),
                                messages: arrayUnion({
                                    role: 'user',
                                    content: question,
                                    timestamp: new Date().toISOString()
                                }, {
                                    role: 'assistant',
                                    content: text,
                                    timestamp: new Date().toISOString()
                                })
                            }, { merge: true });
                        } catch (err) {
                            console.error('Failed to log chat to Firebase:', err);
                        }
                    },
                });
                console.log(`??Success with ${modelName}`);
                break; // Success, exit loop
            // eslint-disable-next-line @typescript-eslint/no-explicit-any
    } catch (error: any) {
                const isQuotaError = error.message?.includes('429') || error.message?.includes('Quota') || error.message?.includes('RESOURCE_EXHAUSTED');
                if (isQuotaError) {
                    console.warn(`?좑툘 Quota exceeded for ${modelName}, switching to next...`);
                    continue; // Try next model
                }
                throw error; // Other errors, crash
            }
        }
        if (!result) throw new Error('All Gemini models exhausted (Quota Exceeded)');
    } else {
        // --- OpenAI Default ---
        result = await streamText({
            model: openai('gpt-4o-mini'),
            system: systemPrompt,
            messages,
             onFinish: async ({ text }) => {
                try {
                    const sessionId = reqBody.sessionId || 'anonymous_session';
                    await setDoc(doc(db, 'chat_sessions', sessionId), {
                        sessionId: sessionId,
                        persona: reqBody.persona || 'professional',
                        model: 'gpt-4o-mini',
                        provider: 'openai',
                        lastUpdated: serverTimestamp(),
                        messages: arrayUnion({
                            role: 'user',
                            content: question,
                            timestamp: new Date().toISOString()
                        }, {
                            role: 'assistant',
                            content: text,
                            timestamp: new Date().toISOString()
                        })
                    }, { merge: true });
                } catch (err) {
                    console.error('Failed to log chat to Firebase:', err);
                }
            },
        });
    }

    // Return raw text stream for simplified manual client
    return new Response(result.textStream, {
        headers: {
            'Content-Type': 'text/plain; charset=utf-8',
        },
    });

  } catch (error) {
    console.error('??Chat API Error:', error);
    return new Response(JSON.stringify({ error: 'Failed to process chat' }), {
      status: 500,
      headers: { 'Content-Type': 'application/json' },
    });
  }
}

"use client";

// motion, AnimatePresence removed as unused
import { useState, useRef, useEffect } from "react";

interface Props {
    isActive?: boolean;
    scale?: number;
    isEmbedded?: boolean;
}

export function VirtualScrollVisual({ isActive = true, scale = 1 }: Props) {
    const [scrollPos, setScrollPos] = useState(0);
    const containerRef = useRef<HTMLDivElement>(null);
    const [autoScroll, setAutoScroll] = useState(true);
    
    // Simulate extensive list
    const totalItems = 1000;
    const itemHeight = 40;
    const containerHeight = 320;
    const visibleCount = Math.ceil(containerHeight / itemHeight);
    
    // Virtual Scroll Logic Simulation
    const startIndex = Math.floor(scrollPos / itemHeight);
    const endIndex = Math.min(totalItems - 1, startIndex + visibleCount + 1);
    
    const visibleItems = [];
    for (let i = startIndex; i <= endIndex; i++) {
        visibleItems.push({
            index: i,
            top: i * itemHeight,
            // Varying widths for "skeleton" effect
            width: 60 + (i % 5) * 10 
        });
    }

    // Auto-scroll effect for the card visual
    useEffect(() => {
        if (!isActive || !autoScroll) return;
        
        const interval = setInterval(() => {
            if (containerRef.current) {
                // Scroll down and loop back
                const newPos = (scrollPos + 2) % (totalItems * itemHeight - containerHeight);
                setScrollPos(newPos);
                containerRef.current.scrollTop = newPos;
            }
        }, 20);

        return () => clearInterval(interval);
    }, [isActive, autoScroll, scrollPos]);

    return (
        <div style={{ transform: `scale(${scale})`, transformOrigin: 'center center' }} className="w-full h-full flex items-center justify-center">
             <div className="bg-white rounded-2xl shadow-xl border border-gray-100 overflow-hidden relative w-[320px] h-[400px] flex flex-col">
                {/* Header */}
                <div className="h-12 border-b border-gray-100 flex items-center px-4 gap-2 bg-gray-50/50">
                    <div className="w-3 h-3 rounded-full bg-red-400" />
                    <div className="w-3 h-3 rounded-full bg-amber-400" />
                    <div className="w-3 h-3 rounded-full bg-green-400" />
                    <div className="ml-auto text-xs text-gray-400 font-mono">VirtualGrid Engine</div>
                </div>

                {/* Grid Body with Scroll */}
                <div 
                    ref={containerRef}
                    className="flex-1 overflow-y-auto relative no-scrollbar"
                    // Disable manual scroll if auto-scroll is intended, or allow user to take over
                    onMouseEnter={() => setAutoScroll(false)}
                    onMouseLeave={() => setAutoScroll(true)}
                    onScroll={(e) => {
                         if (!autoScroll) {
                             setScrollPos((e.target as HTMLDivElement).scrollTop);
                         }
                    }}
                >
                    {/* Phantom Helper (Fake Height) */}
                    <div style={{ height: totalItems * itemHeight, position: 'relative' }}>
                        
                        {/* Rendered Items Only */}
                        <AnimatePresence>
                            {visibleItems.map((item) => (
                                <div
                                    key={item.index}
                                    style={{
                                        position: 'absolute',
                                        top: 0,
                                        left: 0,
                                        right: 0,
                                        height: itemHeight,
                                        transform: `translateY(${item.top}px)`
                                    }}
                                    className="border-b border-gray-50 flex items-center px-4 hover:bg-emerald-50/30 transition-colors"
                                >
                                    <div className="w-12 text-xs text-gray-400 font-mono font-medium">
                                        #{item.index + 1}
                                    </div>
                                    <div className="flex-1 flex gap-3">
                                        <div className="h-2 bg-gray-100 rounded-full w-full" style={{ width: `${item.width}%` }} />
                                    </div>
                                </div>
                            ))}
                        </AnimatePresence>
                    </div>
                </div>
                
                {/* Overlay Stats */}
                <div className="absolute top-16 right-4 bg-white/90 backdrop-blur shadow-lg rounded-lg p-3 border border-gray-100 text-[10px] font-mono space-y-1 pointer-events-none">
                     <div className="flex justify-between gap-4">
                        <span className="text-gray-500">Nodes</span>
                        <span className="font-bold text-emerald-600">{visibleItems.length}</span>
                    </div>
                </div>
            </div>
        </div>
    );
}

"use client";

import { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import {
    Smartphone,
    FileSpreadsheet,
    Server,
    Zap,
    Monitor,
    Radio
} from "lucide-react";

const PIPELINE_STEPS = [
    {
        id: "appsheet",
        label: "?꾩옣 ?낅젰 (Tablet)",
        icon: Smartphone,
        title: "?쒕툝由??묒닔 ?쒖뒪??

\"源泥좎닔?? 源泥좎닔???ㅼ쓬 寃?щ뒗 ?ъ쟾?꾩엯?덈떎.\" ?쇰뒗 ?뚯꽦 ?덈궡媛 ?섏삤硫? 蹂댁“?먮뱾???대떦 李몄뿬?먮? ?몄??섍퀬 利됱떆 ?ㅼ쓬 寃?ъ떎濡??덈궡?⑸땲??"
    }
];

export function ArchitectureDiagram() {
    const [activeStep, setActiveStep] = useState(0);
    const [isPaused, setIsPaused] = useState(false);

    useEffect(() => {
        if (isPaused) return;

        const timer = setInterval(() => {
            setActiveStep((prev) => (prev + 1) % PIPELINE_STEPS.length);
        }, 3500); // 3.5珥?媛꾧꺽?쇰줈 議곌툑 ?ъ쑀?덇쾶

        return () => clearInterval(timer);
    }, [isPaused]);

    const handleStepClick = (index: number) => {
        setActiveStep(index);
        setIsPaused(true);
        setTimeout(() => setIsPaused(false), 10000); // ?대┃ ??10珥덇컙 ?쇱떆?뺤? (?띿뒪???쎌쓣 ?쒓컙 ?뺣낫)
    };

    return (
        <section className="py-24 px-6 bg-gray-50 border-y border-gray-100">
            <div className="max-w-5xl mx-auto">
                <div className="flex flex-col mb-16 items-center text-center max-w-3xl mx-auto">
                    <motion.h2
                        className="text-3xl md:text-4xl font-extrabold text-gray-900 mb-6 tracking-tight break-keep"
                        initial={{ opacity: 0, y: 10 }}
                        whileInView={{ opacity: 1, y: 0 }}
                        viewport={{ once: true }}
                    >
                        ?곗씠???먮쫫怨??쒖뒪??援ъ“
                    </motion.h2>
                    <motion.p
                        className="text-gray-600 text-lg break-keep leading-relaxed"
                        initial={{ opacity: 0, y: 10 }}
                        whileInView={{ opacity: 1, y: 0 }}
                        viewport={{ once: true }}
                        transition={{ delay: 0.1 }}
                    >
                        ?꾩옣???듭닕???꾧뎄(Google Sheets)瑜?DB濡??ъ슜?섎㈃?쒕룄,<br className="hidden md:block"/>
                        ?ㅼ떆媛꾩꽦(Real-time)???뺣낫?섍린 ?꾪빐 Apps Script瑜?誘몃뱾?⑥뼱濡??쒖슜???섏씠釉뚮━???꾪궎?띿쿂?낅땲??
                    </motion.p>
                </div>

                {/* Interactive Pipeline Container */}
                <div className="flex flex-col gap-16">
                    
                    {/* Top: Progress Bar & Icons */}
                    <div className="px-0 relative w-full">
                        {/* Container with extra padding to prevent clipping of scaled active items */}
                        <div className="flex items-center w-full min-w-[300px] md:min-w-full overflow-x-auto gap-0 pl-1 pr-1 py-12 md:pb-12 [&::-webkit-scrollbar]:hidden [-ms-overflow-style:none] [scrollbar-width:none] touch-pan-x cursor-grab active:cursor-grabbing">
                            {PIPELINE_STEPS.map((step, index) => {
                                const isActive = index === activeStep;
                                const isCompleted = index < activeStep;
                                const Icon = step.icon;
                                const isLast = index === PIPELINE_STEPS.length - 1;

                                return (
                                    <div key={step.id} className="contents">
                                        {/* Step Node */}
                                        <div 
                                            className="flex flex-col items-center gap-6 cursor-pointer group relative z-10 shrink-0"
                                            onClick={() => handleStepClick(index)}
                                            style={{ width: '100px' }} // ?꾩씠肄?媛꾧꺽 議곌툑 ???뺣낫
                                        >
                                            <motion.div 
                                                className={`relative w-14 h-14 md:w-16 md:h-16 rounded-2xl flex items-center justify-center transition-all duration-500 ease-out z-20
                                                    ${isActive ? "bg-blue-600 shadow-xl shadow-blue-200 scale-110 rotate-3" : 
                                                      isCompleted ? "bg-white border-2 border-blue-600" : "bg-white border-2 border-gray-200 group-hover:border-gray-400"}`}
                                            >
                                                <Icon 
                                                    className={`w-6 h-6 md:w-7 md:h-7 transition-colors duration-500
                                                        ${isActive ? "text-white" : isCompleted ? "text-blue-600" : "text-gray-400"}`} 
                                                    strokeWidth={2}
                                                />
                                            </motion.div>
                                            
                                            <span className={`absolute top-full mt-5 w-32 text-xs md:text-sm font-bold break-keep text-center transition-colors duration-500 ${isActive ? "text-blue-700" : "text-gray-500"}`}>
                                                {step.label}
                                            </span>
                                        </div>

                                        {/* Connecting Line */}
                                        {!isLast && (
                                            <div className="flex-1 h-[2px] bg-gray-200 mx-2 relative overflow-hidden shrink-0 min-w-[40px] rounded-full">
                                                {/* Fill line */}
                                                <motion.div 
                                                    className="absolute inset-y-0 left-0 bg-blue-500"
                                                    initial={{ width: "0%" }}
                                                    animate={{ width: index < activeStep ? "100%" : "0%" }}
                                                    transition={{ duration: 0.4, ease: "easeInOut" }}
                                                />
                                            </div>
                                        )}
                                    </div>
                                );
                            })}
                        </div>
                    </div>

                    {/* Bottom: Dynamic Detail Card */}
                    <div className="relative h-[200px] w-full max-w-3xl mx-auto">
                        <AnimatePresence mode="wait">
                            <motion.div
                                key={activeStep}
                                className="absolute inset-0 flex flex-col items-center text-center justify-start pt-4 px-4 bg-white rounded-3xl border border-gray-100 shadow-lg p-8"
                                initial={{ opacity: 0, y: 10, scale: 0.98 }}
                                animate={{ opacity: 1, y: 0, scale: 1 }}
                                exit={{ opacity: 0, y: -10, scale: 0.98 }}
                                transition={{ duration: 0.3, ease: "easeOut" }}
                            >
                                <div className="inline-flex items-center gap-2 mb-4 px-3 py-1 bg-blue-50 text-blue-700 text-xs font-bold rounded-full uppercase tracking-wide">
                                    Step {activeStep + 1}
                                </div>
                                <h3 className="text-2xl md:text-3xl font-bold text-gray-900 mb-4 break-keep tracking-tight">
                                    {PIPELINE_STEPS[activeStep].title}
                                </h3>
                                
                                <p className="text-gray-600 text-base md:text-lg leading-relaxed break-keep max-w-2xl">
                                    {PIPELINE_STEPS[activeStep].desc}
                                </p>
                            </motion.div>
                        </AnimatePresence>
                    </div>
                </div>
            </div>
        </section>
    );
}

"use client";

import { motion } from "framer-motion";
import { Users, AlertCircle, Zap } from "lucide-react";

import { useTranslations } from "next-intl";

export function BusinessContext() {
    const t = useTranslations("CohortDashboard.BusinessContext");

    return (
        <section className="py-16 md:py-24 bg-slate-50 border-y border-slate-100">
            <div className="max-w-4xl mx-auto px-6">
                <motion.div 
                    initial={{ opacity: 0, y: 20 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    viewport={{ once: true }}
                    className="text-center mb-12"
                >
                    <h2 className="text-2xl md:text-3xl font-bold text-gray-900 mb-4 tracking-tight">
                        {t('title')}
                    </h2>
                    <p className="text-gray-600 text-base md:text-lg leading-relaxed break-keep"
                        dangerouslySetInnerHTML={{ __html: t.raw('description') }}
                    />
                </motion.div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    {/* Problem Card */}
                    <motion.div 
                        initial={{ opacity: 0, x: -20 }}
                        whileInView={{ opacity: 1, x: 0 }}
                        viewport={{ once: true }}
                        className="bg-red-50/70 rounded-2xl p-6 border border-red-100"
                    >
                        <div className="flex items-center gap-3 mb-4">
                            <div className="w-10 h-10 rounded-full bg-red-100 flex items-center justify-center">
                                <AlertCircle className="w-5 h-5 text-red-600" />
                            </div>
                            <h3 className="font-bold text-gray-900">{t('problemTitle')}</h3>
                        </div>
                        <p className="text-gray-600 text-sm leading-relaxed break-keep"
                            dangerouslySetInnerHTML={{ __html: t.raw('problemDesc') }}
                        />
                    </motion.div>

                    {/* Solution Card */}
                    <motion.div 
                        initial={{ opacity: 0, x: 20 }}
                        whileInView={{ opacity: 1, x: 0 }}
                        viewport={{ once: true }}
                        className="bg-emerald-50/70 rounded-2xl p-6 border border-emerald-100"
                    >
                        <div className="flex items-center gap-3 mb-4">
                            <div className="w-10 h-10 rounded-full bg-emerald-100 flex items-center justify-center">
                                <Zap className="w-5 h-5 text-emerald-600" />
                            </div>
                            <h3 className="font-bold text-gray-900">{t('solutionTitle')}</h3>
                        </div>
                        <p className="text-gray-600 text-sm leading-relaxed break-keep"
                            dangerouslySetInnerHTML={{ __html: t.raw('solutionDesc') }}
                        />
                    </motion.div>
                </div>
            </div>
        </section>
    );
}

