        </motion.div>
    );
}

"use client";

import { motion, AnimatePresence } from "framer-motion";
import { User, Check, X, Clock, Volume2 } from "lucide-react";
import { useTranslations } from "next-intl";

interface LogicFlowAnimationProps {
    step: number;
    isPaused: boolean;
    onResume: () => void;
    candidates: any[];
    room: string;
}

export function LogicFlowAnimation({ step, isPaused, onResume, candidates, room }: LogicFlowAnimationProps) {
    const t = useTranslations("CohortDashboard.LogicFlow");
    const winner = candidates.find(c => c.id === 4);

    return (
        <div className="bg-slate-50 rounded-[2rem] md:rounded-[2.5rem] p-4 md:p-8 border border-slate-200 relative h-[500px] md:h-[580px] flex flex-col shadow-inner overflow-hidden transition-all duration-300">
            {/* Background Pattern */}
            <div className="absolute inset-0 opacity-[0.03] pointer-events-none" 
                 style={{ backgroundImage: 'radial-gradient(#000 1px, transparent 1px)', backgroundSize: '24px 24px' }} />

            <div className="relative z-10 flex flex-col h-full">
                {/* Header */}
                <div className="flex justify-between items-center mb-6 md:mb-8 shrink-0">
                    <div className="flex items-center gap-2">
                        <div className="w-1.5 h-1.5 md:w-2 md:h-2 rounded-full bg-blue-500 animate-pulse" />
                        <span className="text-[11px] md:text-xs font-bold text-slate-500 tracking-wider">{t('header')}</span>
                    </div>
                    <div className="px-2.5 py-1 bg-white rounded-full border border-slate-200 text-[10px] font-mono font-bold text-slate-400">
                        {t('phase')}: {step}/4
                    </div>
                </div>

                {/* Content Area - Using LayoutGroup/AnimatePresence for smooth swapping */}
                <div className="flex-1 flex flex-col gap-3 md:gap-4">
                    <AnimatePresence mode="popLayout">
                        {candidates.map((person) => {
                            const isBlockedByStatus = step >= 1 && person.status === "Active";
                            const isBlockedByPrereq = step >= 2 && !isBlockedByStatus && !person.prereq;
                            const isWinner = person.id === 4;
                            
                            // In step 4, we only show the winner
                            if (step === 4 && !isWinner) return null;

                            return (
                                <motion.div
                                    key={person.id}
                                    layout
                                    initial={{ opacity: 0, scale: 0.9, y: 20 }}
                                    animate={{ 
                                        opacity: (isBlockedByStatus || isBlockedByPrereq) ? 0.4 : 1,
                                        scale: 1, 
                                        y: 0,
                                        filter: (isBlockedByStatus || isBlockedByPrereq) ? "grayscale(0.4)" : "none"
                                    }}
                                    exit={{ opacity: 0, scale: 0.8, x: -20 }}
                                    transition={{ type: "spring", stiffness: 300, damping: 30 }}
                                    className={`bg-white items-center justify-between border-2
                                        ${(step === 4 && isWinner) 
                                            ? "p-6 md:p-8 rounded-[1.5rem] md:rounded-[2rem] border-blue-500 shadow-xl flex" 
                                            : "p-3 md:p-4 rounded-xl md:rounded-2xl border-slate-100 shadow-sm flex"}
                                        ${(isBlockedByStatus || isBlockedByPrereq) ? "bg-slate-50/50" : "bg-white"}
                                    `}
                                >
                                    <div className="flex items-center gap-3 md:gap-5">
                                        <div className={`flex items-center justify-center font-bold shrink-0 transition-colors duration-300
                                            ${(step === 4 && isWinner) 
                                                ? "w-14 h-14 md:w-16 md:h-16 rounded-xl md:rounded-2xl text-lg md:text-xl bg-blue-100 text-blue-700" 
                                                : "w-10 h-10 md:w-12 md:h-12 rounded-lg md:rounded-xl text-xs md:text-sm bg-slate-100 text-slate-400"}`}>
                                            {step >= 3 && isWinner ? "1st" : <User className="w-4 h-4 md:w-5 md:h-5 text-slate-400" />}
                                        </div>
                                        <div>
                                            <div className={`font-bold text-slate-900 flex items-center gap-1.5 md:gap-2 transition-all duration-300 ${(step === 4 && isWinner) ? "text-xl md:text-2xl" : "text-sm md:text-base"}`}>
                                                {person.name}
                                                {isBlockedByStatus && <span className="text-[9px] md:text-[10px] bg-red-50 text-red-500 px-1.5 py-0.5 rounded-full whitespace-nowrap">{t('status.active')}</span>}
                                                {isBlockedByPrereq && <span className="text-[9px] md:text-[10px] bg-orange-50 text-orange-500 px-1.5 py-0.5 rounded-full whitespace-nowrap">{t('status.locked')}</span>}
                                            </div>
                                            <div className={`text-slate-500 flex items-center gap-1.5 md:gap-2 mt-0.5 md:mt-1 transition-all duration-300 ${(step === 4 && isWinner) ? "text-xs md:text-sm" : "text-[10px] md:text-xs"}`}>
                                                <Clock className={(step === 4 && isWinner) ? "w-3.5 h-3.5 md:w-4 md:h-4" : "w-3 h-3"} /> {person.time}
                                            </div>
                                        </div>
                                    </div>

                                    <div className="flex items-center gap-2 md:gap-3">
                                        {(isBlockedByStatus || isBlockedByPrereq) ? (
                                            <div className="flex items-center gap-1 md:gap-1.5 px-2 py-1 md:px-3 md:py-1.5 bg-slate-100 rounded-lg text-[10px] md:text-[11px] font-bold text-slate-400 whitespace-nowrap">
                                                <X className="w-3 h-3 md:w-3.5 md:h-3.5" />
                                                {person.reason}
                                            </div>
                                        ) : step >= 1 && (
                                            <motion.div initial={{ scale: 0 }} animate={{ scale: 1 }} className={`flex items-center gap-1 md:gap-1.5 rounded-lg font-bold text-emerald-600 bg-emerald-50 whitespace-nowrap
                                                ${(step === 4 && isWinner) ? "px-3 py-1.5 md:px-4 md:py-2 text-xs md:text-sm" : "px-2 py-1 md:px-3 md:py-1.5 text-[10px] md:text-[11px]"}`}>
                                                <Check className={(step === 4 && isWinner) ? "w-3.5 h-3.5 md:w-4 md:h-4" : "w-3 h-3 md:w-3.5 md:h-3.5"} /> {t('status.waiting')}
                                            </motion.div>
                                        )}
                                    </div>
                                </motion.div>
                            );
                        })}

                        {step === 4 && winner && (
                            <motion.div 
                                layout
                                initial={{ opacity: 0, y: 50, scale: 0.9 }}
                                animate={{ opacity: 1, y: 0, scale: 1 }}
                                exit={{ opacity: 0, y: 20 }}
                                transition={{ type: "spring", stiffness: 300, damping: 30, delay: 0.1 }}
                                className="mt-4" // slight margin top
                            >
                                <div className="bg-blue-600 rounded-[2rem] p-8 text-white relative overflow-hidden shadow-2xl shadow-blue-500/40">
                                    {/* Animated Waves */}
                                    <div className="absolute top-0 right-0 p-8 flex gap-1 items-end h-full opacity-20 group">
                                        {[0.4, 0.7, 0.5, 0.9, 0.6].map((h, i) => (
                                            <motion.div 
                                                key={i}
                                                className="w-2 bg-white rounded-full"
                                                animate={{ height: ['20%', '80%', '20%'] }}
                                                transition={{ repeat: Infinity, duration: 1, delay: i * 0.1 }}
                                            />
                                        ))}
                                    </div>

                                    <div className="relative z-10">
                                        <div className="flex items-center gap-3 mb-4 opacity-70">
                                            <Volume2 className="w-5 h-5" />
                                            <span className="text-xs font-bold tracking-widest uppercase">{t('ttsLabel')}</span>
                                        </div>
                                        <h4 className="text-2xl font-black mb-2 leading-tight" dangerouslySetInnerHTML={{ __html: t('ttsMessage', {name: winner.name, room: room}) }} />
                                        <div className="inline-block px-3 py-1 bg-white/20 rounded-full text-[10px] font-bold">
                                            {t('ttsActive')}
                                        </div>
                                    </div>
                                </div>
                            </motion.div>
                        )}
                    </AnimatePresence>
                </div>


            </div>
        </div>
    );
}

"use client";

import { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import {
  Smartphone,
  FileSpreadsheet,
  Server,
  Zap,
  Monitor,
  Database,
} from "lucide-react";

import { useTranslations } from "next-intl";

export function TechStackFlow() {
  const t = useTranslations("CohortDashboard.TechStack");
  const [activeStep, setActiveStep] = useState(0);
  const [isPaused, setIsPaused] = useState(false);

  const PIPELINE_STEPS = [
    {
      id: "appsheet",
      label: t("steps.appsheet.label"),
      icon: Smartphone,
      title: t("steps.appsheet.title"),
      desc: t("steps.appsheet.desc"),
    },
    {
      id: "sheets",
      label: t("steps.sheets.label"),
      icon: FileSpreadsheet,
      title: t("steps.sheets.title"),
      desc: t("steps.sheets.desc"),
    },
    {
      id: "appsscript",
      label: t("steps.appsscript.label"),
      icon: Server,
      title: t("steps.appsscript.title"),
      desc: t("steps.appsscript.desc"),
    },
    {
      id: "firebase",
      label: t("steps.firebase.label"),
      icon: Database,
      title: t("steps.firebase.title"),
      desc: t("steps.firebase.desc"),
    },
    {
      id: "dashboard",
      label: t("steps.dashboard.label"),
      icon: Monitor,
      title: t("steps.dashboard.title"),
      desc: t("steps.dashboard.desc"),
    },
  ];

  useEffect(() => {
    if (isPaused) return;

    const timer = setInterval(() => {
      setActiveStep((prev) => (prev + 1) % PIPELINE_STEPS.length);
    }, 3500); // 3.5珥?媛꾧꺽?쇰줈 議곌툑 ?ъ쑀?덇쾶

    return () => clearInterval(timer);
  }, [isPaused, PIPELINE_STEPS.length]);

  const handleStepClick = (index: number) => {
    setActiveStep(index);
    setIsPaused(true);
    setTimeout(() => setIsPaused(false), 10000); // ?대┃ ??10珥덇컙 ?쇱떆?뺤? (?띿뒪???쎌쓣 ?쒓컙 ?뺣낫)
  };

  return (
    <section className="py-12 md:py-24 px-6 bg-gray-50 border-y border-gray-100">
      <div className="max-w-5xl mx-auto">
        <div className="flex flex-col mb-8 md:mb-16 items-center text-center max-w-3xl mx-auto">
          <motion.h2
            className="text-3xl md:text-4xl font-extrabold text-gray-900 mb-6 tracking-tight break-keep"
            initial={{ opacity: 0, y: 10 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
          >
            {t("title")}
          </motion.h2>
          <motion.p
            className="text-gray-600 text-lg break-keep leading-relaxed"
            initial={{ opacity: 0, y: 10 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ delay: 0.1 }}
            dangerouslySetInnerHTML={{ __html: t.raw("description") }}
          />
        </div>

        {/* Interactive Pipeline Container */}
        <div className="flex flex-col gap-8 md:gap-16">
          {/* Top: Progress Bar & Icons */}
          <div className="px-0 relative w-full">
            {/* Container with extra padding to prevent clipping of scaled active items and text labels */}
            {/* MOBILE LAYOUT: Single Focused Step (Carousel) */}
            <div className="md:hidden w-full flex flex-col items-center gap-4 mb-4 min-h-[160px] justify-center">
              {/* Active Icon Body */}
              <div className="flex flex-col items-center gap-3">
                <motion.div
                  key={activeStep}
                  initial={{ scale: 0.8, opacity: 0 }}
                  animate={{ scale: 1, opacity: 1 }}
                  transition={{ type: "spring", stiffness: 300, damping: 25 }}
                  className="relative w-20 h-20 rounded-3xl bg-blue-600 flex items-center justify-center shadow-xl shadow-blue-200 z-10"
                >
                  {/* Render Active Icon */}
                  {(() => {
                    const ActiveIcon = PIPELINE_STEPS[activeStep].icon;
                    return (
                      <ActiveIcon
                        className="w-9 h-9 text-white"
                        strokeWidth={2}
                      />
                    );
                  })()}
                </motion.div>
                <span className="text-sm font-bold text-blue-700">
                  {PIPELINE_STEPS[activeStep].label}
                </span>
              </div>

              {/* Dots Indicator */}
              <div className="flex gap-2">
                {PIPELINE_STEPS.map((_, idx) => (
                  <button
                    key={idx}
                    onClick={() => handleStepClick(idx)}
                    className={`w-2 h-2 rounded-full transition-all duration-300 
                                            ${
                                              idx === activeStep
                                                ? "bg-blue-600 w-6"
                                                : "bg-gray-300"
                                            }`}
                  />
                ))}
              </div>
            </div>

            {/* DESKTOP LAYOUT: Horizontal Linear Flow (Hidden on Mobile) */}
            <div className="hidden md:flex flex-row items-center justify-between w-full pt-12 pb-20 relative gap-0">
              {PIPELINE_STEPS.map((step, index) => {
                const isActive = index === activeStep;
                const isCompleted = index < activeStep;
                const Icon = step.icon;
                const isLast = index === PIPELINE_STEPS.length - 1;

                return (
                  <div key={step.id} className="contents">
                    {/* Step Node */}
                    <div
                      className="flex flex-col items-center gap-6 cursor-pointer group relative z-10 p-0"
                      onClick={() => handleStepClick(index)}
                    >
                      <motion.div
                        className={`relative w-16 h-16 rounded-2xl flex items-center justify-center transition-all duration-500 ease-out z-20 shrink-0
                                                    ${
                                                      isActive
                                                        ? "bg-blue-600 shadow-xl shadow-blue-200 scale-110"
                                                        : isCompleted
                                                        ? "bg-white border-2 border-blue-600"
                                                        : "bg-white border-2 border-gray-200 group-hover:border-gray-400"
                                                    }`}
                      >
                        <Icon
                          className={`w-7 h-7 transition-colors duration-500
                                                        ${
                                                          isActive
                                                            ? "text-white"
                                                            : isCompleted
                                                            ? "text-blue-600"
                                                            : "text-gray-400"
                                                        }`}
                          strokeWidth={2}
                        />
                      </motion.div>

                      <span
                        className={`absolute top-full left-1/2 -translate-x-1/2 mt-5 w-48 text-center text-sm font-bold transition-colors duration-500
                                                ${
                                                  isActive
                                                    ? "text-blue-700"
                                                    : "text-gray-500"
                                                }`}
                      >
                        {step.label}
                      </span>
                    </div>

                    {/* Connecting Line */}
                    {!isLast && (
                      <div className="flex-1 h-[2px] bg-gray-200 mx-4 relative overflow-hidden shrink-0 min-w-[40px] rounded-full">
                        <motion.div
                          className="absolute inset-0 bg-blue-500"
                          initial={{ width: "0%" }}
                          animate={{
                            width: index < activeStep ? "100%" : "0%",
                          }}
                          transition={{ duration: 0.4 }}
                        />
                      </div>
                    )}
                  </div>
                );
              })}
            </div>
          </div>

          {/* Bottom: Dynamic Detail Card */}
          <div className="relative h-[240px] md:h-[200px] w-full max-w-3xl mx-auto">
            <AnimatePresence mode="wait">
              <motion.div
                key={activeStep}
                className="absolute inset-0 flex flex-col items-center text-center justify-start pt-4 px-4"
                initial={{ opacity: 0, y: 10, scale: 0.98 }}
                animate={{ opacity: 1, y: 0, scale: 1 }}
                exit={{ opacity: 0, y: -10, scale: 0.98 }}
                transition={{ duration: 0.3, ease: "easeOut" }}
              >
                <div className="inline-flex items-center gap-2 mb-4 px-3 py-1 bg-blue-50 text-blue-700 text-xs font-bold rounded-full uppercase tracking-wide">
                  Step {activeStep + 1}
                </div>
                <h3 className="text-2xl md:text-3xl font-bold text-gray-900 mb-4 break-keep tracking-tight">
                  {PIPELINE_STEPS[activeStep].title}
                </h3>

                <p className="text-gray-600 text-base md:text-lg leading-relaxed break-keep max-w-2xl">
                  {PIPELINE_STEPS[activeStep].desc}
                </p>
              </motion.div>
            </AnimatePresence>
          </div>
        </div>
      </div>
    </section>
  );
}

"use client";

import { motion } from "framer-motion";
import { useTranslations } from "next-intl";

interface StatusBoardProps {
    boardState: any;
    blinkingStation: string | null;
    blinkToggle: boolean;
    showNextUpdate: boolean;
    currentExam: any;
}

export function StatusBoard({ 
    boardState, 
    blinkingStation, 
    blinkToggle, 
    showNextUpdate, 
    currentExam 
}: StatusBoardProps) {
    const t = useTranslations("CohortDashboard");

    return (
        <div className="w-full h-full flex flex-col bg-white font-sans overflow-hidden rounded-md">
            {/* Browser-like Address Bar - Safari style */}
            <div className="shrink-0 bg-gradient-to-b from-gray-50 to-gray-100 border-b border-gray-200 px-3 py-1">
                <div className="flex items-center bg-white rounded-md border border-gray-200 px-2 py-0.5">
                    <svg className="w-3 h-3 text-green-500 mr-1.5 shrink-0" fill="currentColor" viewBox="0 0 20 20">
                        <path fillRule="evenodd" d="M5 9V7a5 5 0 0110 0v2a2 2 0 012 2v5a2 2 0 01-2 2H5a2 2 0 01-2-2v-5a2 2 0 012-2zm8-2v2H7V7a3 3 0 016 0z" clipRule="evenodd" />
                    </svg>
                    <span className="flex-1 text-xs text-gray-600">{t('ExamFlow.StatusBoard.header')}</span>
                    <svg className="w-3 h-3 text-gray-400 ml-1.5 shrink-0" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M14 5l7 7m0 0l-7 7m7-7H3" />
                    </svg>
                </div>
            </div>

            {/* SNSB-C Header */}
            <div className="shrink-0 py-1 text-center border-b-2 border-gray-200">
                <span className="text-[#2c5c8e] font-bold text-xl">SNSB-C</span>
            </div>

            {/* Exam Rooms Section (SNSB-C 1, 2, 3) */}
            <div className="flex border-b-2 border-gray-200 shrink-0" style={{ height: '32%' }}>
                {boardState.snsbcRooms.map((room: any) => {
                    const roomId = `snsbc-${room.roomNum}`;
                    const isBlinking = blinkingStation === roomId;
                    const showAsActive = isBlinking ? blinkToggle : true;
                    
                    return (
                        <div 
                            key={room.roomNum}
                            className="flex-1 flex flex-col justify-center items-center border-r border-gray-200 last:border-r-0 p-1"
                            style={{ 
                                background: showAsActive 
                                    ? 'linear-gradient(to bottom, #4c8fbd 0%, #2c5c8e 100%)'
                                    : '#ffffff',
                                transition: isBlinking ? 'background 0.2s ease' : 'none'
                            }}
                        >
                            <div 
                                className="text-sm font-bold"
                                style={{ 
                                    color: showAsActive ? '#ffffff' : '#2c5c8e',
                                    transition: isBlinking ? 'color 0.2s ease' : 'none'
                                }}
                            >
                                {room.roomNum}{t('ExamFlow.StatusBoard.roomSuffix')}
                            </div>
                            <div 
                                className="text-3xl font-bold"
                                style={{ 
                                    color: showAsActive ? '#ffffff' : '#333',
                                    transition: isBlinking ? 'color 0.2s ease' : 'none'
                                }}
                            >
                                {isBlinking && showNextUpdate
                                    ? (currentExam.type === "start" ? currentExam.currentPerson : "-")
                                    : room.current
                                }
                            </div>
                            <div 
                                className="text-sm"
                                style={{ 
                                    color: showAsActive ? 'rgba(255,255,255,0.8)' : '#666',
                                    transition: isBlinking ? 'color 0.2s ease' : 'none'
                                }}
                            >
                                {room.time}
                            </div>
                        </div>
                    );
                })}
            </div>

            {/* Next Subject Section (for SNSB-C) */}
            <div className="shrink-0 py-1 text-center border-b-2 border-gray-200 bg-white">
                <span className="text-gray-500 text-sm font-bold mr-1">{t('ExamFlow.StatusBoard.next')}</span>
                <span 
                    className="text-[#2c5c8e] text-xl font-bold"
                >
                    {boardState.snsbcRooms[0].next}
                </span>
            </div>

            {/* Test Stations Section (?ъ쟾?? 梨꾪삁/梨꾨눊, ?좎껜湲곕뒫) */}
            <div className="flex flex-1 min-h-0">
                {[
                    { id: 'ecg', title: t('Bottleneck.exams.ecg'), data: boardState.stations.ecg },
                    { id: 'blood', title: t('Bottleneck.exams.blood'), data: boardState.stations.blood },
                    { id: 'physical', title: t('Bottleneck.exams.body'), data: boardState.stations.physical }
                ].map((station) => {
                    const isActive = station.data.current !== null;
                    const isBlinking = blinkingStation === station.id;
                    
                    const showAsActive = isBlinking ? (blinkToggle ? !isActive : isActive) : isActive;
                    
                    const bgStyle = showAsActive 
                        ? 'linear-gradient(to bottom, #4c8fbd 0%, #2c5c8e 100%)' 
                        : '#ffffff';
                    const titleBg = showAsActive ? 'rgba(255,255,255,0.1)' : '#f3f4f6';
                    const titleColor = showAsActive ? '#ffffff' : '#2c5c8e';
                    const textColor = showAsActive ? '#ffffff' : '#333333';
                    const subTextColor = showAsActive ? 'rgba(255,255,255,0.8)' : '#666666';
                    const nextBoxBg = showAsActive 
                        ? '#ffffff' 
                        : 'linear-gradient(to bottom, #4c8fbd 0%, #2c5c8e 100%)';
                    const nextLabelColor = showAsActive ? '#6b7280' : 'rgba(255,255,255,0.8)';
                    const nextNameColor = showAsActive ? '#2c5c8e' : '#ffffff';
                    
                    return (
                        <div 
                            key={station.id}
                            className="flex-1 flex flex-col border-r border-gray-200 last:border-r-0 p-1.5"
                            style={{ 
                                background: bgStyle,
                                transition: isBlinking ? 'background 0.2s ease' : 'none'
                            }}
                        >
                            {/* Station Title */}
                            <div 
                                className="text-center text-sm font-bold py-1 rounded"
                                style={{ 
                                    backgroundColor: titleBg, 
                                    color: titleColor,
                                    transition: isBlinking ? 'all 0.2s ease' : 'none'
                                }}
                            >
                                {station.title}
                            </div>

                            {/* Current Person */}
                            <div className="flex-1 flex flex-col justify-center items-center">
                                <div 
                                    className="text-3xl font-bold"
                                    style={{ 
                                        color: textColor,
                                        transition: isBlinking ? 'color 0.2s ease' : 'none'
                                    }}
                                >
                                    {isBlinking && showNextUpdate
                                        ? (currentExam.type === "start" ? currentExam.currentPerson : "-")
                                        : (station.data.current || '-')
                                    }
                                </div>
                                <div 
                                    className="text-sm"
                                    style={{ 
                                        color: subTextColor,
                                        transition: isBlinking ? 'color 0.2s ease' : 'none'
                                    }}
                                >
                                    {station.data.time}
                                </div>
                            </div>

                            {/* Next Person Box */}
                            <div 
                                className="py-1 px-2 mx-4 mb-1.5 text-center rounded"
                                style={{ 
                                    background: nextBoxBg,
                                    transition: isBlinking ? 'background 0.2s ease' : 'none'
                                }}
                            >
                                <div 
                                    className="text-xs font-bold"
                                    style={{ 
                                        color: nextLabelColor,
                                        transition: isBlinking ? 'color 0.2s ease' : 'none'
                                    }}
                                >
                                    {t('ExamFlow.StatusBoard.next')}
                                </div>
                                <div 
                                    className="text-lg font-bold"
                                    style={{ 
                                        color: nextNameColor,
                                        transition: isBlinking ? 'color 0.2s ease' : 'none'
                                    }}
                                >
                                    {isBlinking && showNextUpdate
                                        ? (currentExam.type === "complete" ? currentExam.nextPerson : "-")
                                        : (station.data.next || t('ExamFlow.StatusBoard.noSubject'))
                                    }
                                </div>
                            </div>
                        </div>
                    );
                })}
            </div>

        </div>
    );
}

