    isClicking?: boolean; // For simulating button press visual
    mainButtonLabel?: string;
    showMainButton?: boolean;
}

export const DemoBottomNavigation: React.FC<DemoBottomNavigationProps> = ({
    currentStepIndex,
    totalSteps,
    stepName,
    onNext,
    onPrev,
    isNextDisabled = false,
    isClicking = false,
    mainButtonLabel = "?ㅼ쓬",
    showMainButton = true
}) => {
    // Progress percentage
    const progress = totalSteps > 0 ? ((currentStepIndex) / totalSteps) * 100 : 0;

    return (
        <div className="absolute bottom-0 left-0 right-0 z-50">
            {/* Floating Main Action Button Area */}
            <div className="relative h-0 flex justify-center w-full">
                <AnimatePresence>
                    {showMainButton && (
                        <motion.button
                            initial={{ y: 20, opacity: 0, scale: 0.9 }}
                            animate={{ y: -20, opacity: 1, scale: isClicking ? 0.95 : 1 }}
                            exit={{ y: 20, opacity: 0, scale: 0.9 }}
                            transition={{ type: "spring", stiffness: 300, damping: 25 }}
                            onClick={onNext}
                            disabled={isNextDisabled}
                            className={`
                                absolute bottom-0 
                                flex items-center justify-center gap-2 
                                px-8 py-4 
                                rounded-full 
                                shadow-xl 
                                bg-blue-600 text-white 
                                font-bold text-lg 
                                transition-colors
                                ${isNextDisabled ? 'opacity-50 cursor-not-allowed grayscale' : 'hover:bg-blue-700'}
                            `}
                        >
                            <span className="whitespace-nowrap">{mainButtonLabel}</span>
                            {mainButtonLabel.includes("?꾨즺") ? <Check size={20} strokeWidth={3} /> : <ArrowRight size={20} strokeWidth={3} />}
                        </motion.button>
                    )}
                </AnimatePresence>
            </div>

            {/* Bottom Navigation Bar */}
            <div className="bg-white/90 backdrop-blur-md pb-[env(safe-area-inset-bottom)] rounded-b-[32px]">

                {/* Progress Bar inside Nav */}
                <div className="w-[60%] mx-auto mt-4 mb-2 h-1 bg-gray-100 rounded-full overflow-hidden">
                    <motion.div
                        className="h-full bg-blue-500"
                        initial={{ width: 0 }}
                        animate={{ width: `${progress}%` }}
                        transition={{ duration: 0.5, ease: "easeInOut" }}
                    />
                </div>

                <div className="flex items-center justify-between px-6 py-4">
                    {/* Prev Button */}
                    <button
                        onClick={onPrev}
                        disabled={!onPrev}
                        className={`p-2 rounded-full hover:bg-gray-100 transition-colors text-gray-400 ${!onPrev ? 'opacity-30 cursor-default' : ''}`}
                    >
                        <ChevronUp size={24} />
                    </button>

                    {/* Step Indicator */}
                    <div className="text-xs font-medium text-gray-400">
                        {currentStepIndex} / {totalSteps}
                    </div>

                    {/* Next Button (Small) */}
                    <button
                        onClick={onNext}
                        disabled={isNextDisabled}
                        className={`
                            p-3 rounded-full 
                            bg-gray-100 text-gray-600
                            hover:bg-gray-200
                            transition-all active:scale-95
                            ${isNextDisabled ? 'opacity-50' : 'shadow-sm'}
                        `}
                    >
                        <ChevronDown size={24} />
                    </button>
                </div>
            </div>
        </div>
    );
};

"use client";

import React, { useEffect, useRef } from 'react';

export const QRCodeGenerator = ({ data }: { data: string }) => {
    const ref = useRef<HTMLDivElement>(null);
    const qrCode = useRef<any>(null);

    useEffect(() => {
        let mounted = true;
        import('qr-code-styling').then((QRCodeStylingModule) => {
            if (!mounted) return;
            const QRCodeStyling = QRCodeStylingModule.default;
            if (ref.current) ref.current.innerHTML = '';
            qrCode.current = new QRCodeStyling({
                width: 200,
                height: 200,
                type: "svg",
                data: data,
                dotsOptions: { color: "#000000", type: "rounded" },
                cornersSquareOptions: { color: "#000000", type: "extra-rounded" },
                backgroundOptions: { color: "#ffffff" },
                imageOptions: { crossOrigin: "anonymous", margin: 10 },
                margin: 0
            });
            if (ref.current) qrCode.current.append(ref.current);
        });
        return () => { mounted = false; };
    }, [data]);

    return <div ref={ref} className="flex justify-center items-center" />;
};

/**
 * @fileoverview PartySolutionDemo - Interactive multi-scene demo for Party Event SaaS.
 * 
 * This component orchestrates a multi-step demo showcasing the complete
 * event management workflow: customer application ??admin dashboard ??QR check-in.
 * 
 * @module features/portfolio/projects/party-event-saas/demo
 * @see {@link UI_CONSTANTS} - Configuration for scaling and dimensions
 * @see {@link SCENES} - Scene definitions with components and URLs
 */

"use client";

import { useState, useEffect } from 'react';
import { AnimatePresence } from 'framer-motion';
import { cn } from '@/lib/utils';
import { useMobile } from '@/hooks/useMobile';
import { BrowserFrame } from '@/features/portfolio/components/BrowserFrame';

import { UI_CONSTANTS, SCENES } from './config';
import { DemoSceneWrapper } from './components/DemoSceneWrapper';
import type { PartySolutionDemoProps } from './types';

/**
 * Multi-scene demo component for Party Event SaaS portfolio.
 * 
 * Demonstrates the complete event management workflow:
 * 1. **Customer View**: Mobile application form with payment
 * 2. **Admin Dashboard**: Backend management with participant list
 * 3. **QR Check-in**: Entry verification system
 * 
 * @component
 * @example
 * // Embedded in portfolio page
 * <PartySolutionDemo isEmbedded scale={0.8} />
 * 
 * @example
 * // With pause control
 * <PartySolutionDemo isActive={isPlaying} />
 * 
 * @remarks
 * - Scene transitions happen automatically when each scene calls `onComplete`
 * - Mobile view uses different scale multiplier for admin scene
 * - Step indicator shows current scene title
 */
export function PartySolutionDemo({
    isEmbedded = false,
    scale,
    className,
    isActive = true,
}: PartySolutionDemoProps) {
    const [sceneIndex, setSceneIndex] = useState(0);
    const isMobile = useMobile();

    // No auto-reset. Just pause.
    
    /**
     * Advances to the next scene in the demo cycle.
     * Wraps around to first scene after the last.
     */
    const nextScene = () => setSceneIndex(prev => (prev + 1) % SCENES.length);
    
    // Use current sceneIndex regardless of active state (Pause behavior)
    const currentScene = SCENES[sceneIndex];

    // Scale Logic
    const isAdminScene = sceneIndex === 1; // Admin is always index 1 in current config
    const adminScaleMultiplier = (isMobile && isAdminScene) ? UI_CONSTANTS.SCALE.ADMIN_MOBILE_MULTIPLIER : 1;
    const baseScale = scale ?? (isEmbedded ? UI_CONSTANTS.SCALE.EMBEDDED_DEFAULT : 1);
    const currentScale = baseScale * adminScaleMultiplier;

    // Dimensions Logic
    const frameWidth = (isMobile && currentScene.mobileWidth) 
        ? currentScene.mobileWidth 
        : (currentScene.width || UI_CONSTANTS.DIMENSIONS.MOBILE_WIDTH);

    return (
        <div className={cn(
            "relative w-full flex overflow-hidden",
            isEmbedded ? "h-full" : "max-w-[1200px] h-[800px] min-h-screen bg-[#e5e5e5] p-8 font-sans",
            // The parent wrapper alignment matters less now as children are absolute positioned or controlled,
            // but keeping it centered is safe.
            "items-center justify-center",
            className
        )}>
            {/* Main Content Area - Static Centered Container */}
            <div 
                className={cn(
                    "relative w-full h-full flex items-center justify-center transition-all duration-500"
                )}
                style={{ transformOrigin: 'center' }}
            >
                <AnimatePresence mode="wait">
                    <DemoSceneWrapper
                        key={currentScene.id}
                        paramKey={currentScene.id}
                        isMobile={isMobile}
                        isAdminScene={isAdminScene}
                    >
                        <BrowserFrame
                            width={frameWidth}
                            height={UI_CONSTANTS.DIMENSIONS.HEIGHT}
                            url={currentScene.url}
                            isMobile={currentScene.isMobileFrame}
                            hideAddressBar={currentScene.hideAddressBar}
                            uiScale={currentScale}
                        >
                            <currentScene.component 
                                onComplete={nextScene} 
                                isActive={isActive}
                            />
                        </BrowserFrame>
                    </DemoSceneWrapper>
                </AnimatePresence>
            </div>

            {/* Steps Indicator (Embedded or Full Page) */}
            {isEmbedded ? (
                <div className="absolute top-4 left-4 z-50 pointer-events-none">
                    <h3 className="inline-block font-bold text-gray-500/80 text-[10px] uppercase tracking-wider backdrop-blur-md bg-white/40 px-3 py-1 rounded-full border border-white/20 shadow-sm whitespace-nowrap pointer-events-auto">
                        Step {sceneIndex + 1}: {currentScene.title}
                    </h3>
                </div>
            ) : (
                <div className="fixed bottom-8 left-1/2 -translate-x-1/2 text-center space-y-3 z-50">
                    <h3 className="font-bold text-gray-500 text-sm uppercase tracking-wider backdrop-blur-md bg-white/50 px-6 py-2 rounded-full border border-white/20 shadow-sm">
                        Step {sceneIndex + 1}: {currentScene.enTitle}
                    </h3>
                </div>
            )}
        </div>
    );
}

