export const FormScene = ({ onComplete, isActive = true }: { onComplete: () => void; isActive?: boolean }) => {
    const [step, setStep] = useState<'cover' | 'name' | 'phone' | 'schedule' | 'photo' | 'payment' | 'success'>('cover');
