            if (active) await wait(500); // Initial delay
            if (step === 'cover') { await wait(220); if (active) { await click(); await wait(180); if (active) setStep('name'); } }
            if (step === 'name') { await wait(480); if (active) { await click(); await typeName("理쒖씤洹?

010-1234-5678"); await wait(300); if (active) await click(); if (active) setStep('schedule'); } }
