                        <div className={`w-full p-4 rounded-xl border-2 text-left transition-all ${selectedSchedule === 'fri' ? 'border-blue-500 bg-blue-50' : 'border-gray-100'}`} style={{ scale: isClicking && !selectedSchedule ? 0.98 : 1 }}>
